__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Sunset: set LR-Mini-C and D to 80% if B is off",
    "parent": 0,
    "comment": "At sunset turn on secondary lamps only when primary lamp is off; otherwise ensure they are off.",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "<=": { "device": "34 FE BA 2", "status": "ST", "value": 0, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "34 FE BA 3", "command": "WDU", "parameters": [ { "name": "ST", "value": 80, "uom": "51", "precision": 0 } ] },
      { "device": "34 FE BA 4", "command": "WDU", "parameters": [ { "name": "ST", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "34 FE BA 3", "command": "WDU", "parameters": [ { "name": "ST", "value": 0, "uom": "51", "precision": 0 } ] },
      { "device": "34 FE BA 4", "command": "WDU", "parameters": [ { "name": "ST", "value": 0, "uom": "51", "precision": 0 } ] }
    ],
    "reasoning": "Use sunset as schedule. Check LR-Mini-B-LR (34 FE BA 2) status: when it is Off (0) set the two other lamps to 80%; otherwise set them Off to avoid duplicate lighting. Actions use WDU to write the Status (ST) property with UOM 51."
  }
}
__END_NUCORE_ROUTINE__
