__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening Fast-On: propagate to main and side lamps",
    "parent": 0,
    "comment": "When Den-C-D emits a Fast On (DFON) during the evening window, set parent lamp to full and sibling to half",
    "if": [
      { "==": { "device": "3E F5 55 2", "control": "DFON" } },
      { "and": 1 },
      { "from": { "time": "17:00", "to": { "time": "22:00", "day": 0 } } }
    ],
    "then": [
      { "device": "3E F5 55 1", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 2 } ] },
      { "device": "3E F5 55 3", "command": "WDU", "parameters": [ { "name": "ST", "value": 50, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "COC event: use Den-C-D's DFON control as the trigger and limit to a daily evening time window. Use the device-accepted Write Changes (WDU) to set each device's Status (ST) with uom 51 to the specified brightness levels."
  }
}
__END_NUCORE_ROUTINE__
