__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Night beep on manual back door press",
    "parent": 0,
    "comment": "Beep the Heart lock when manually fast-on during night hours",
    "if": [
      { "(": 1 },
        { "==": { "device":"32 91 42 4","control":"DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"32 91 42 4","command":"BEEP","parameters":[{ "name":"n/a","value":0,"uom":"100","precision":0 }] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) on the Heart during nightly hours to trigger an immediate audible confirmation via the Heart's BEEP command. The schedule restricts the response to night (22:00-06:00)."
  }
}
__END_NUCORE_ROUTINE__
