__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday evening double-tap scene",
    "parent": 0,
    "comment": "Double-tap valence fast-on to set evening levels on valence and track",
    "if": [
      { "==": { "device": "48 31 52 1", "control": "DFON", "parameters": [ { "name": "tapCount", "value": 2, "uom": "108", "precision": 0 } ] } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "48 31 52 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 25, "uom": "25", "precision": 0 } ] },
      { "device": "48 31 52 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] },
      { "device": "4C 51 38 1", "command": "RR", "parameters": [ { "name": "n/a", "value": 25, "uom": "25", "precision": 0 } ] },
      { "device": "4C 51 38 1", "command": "OL", "parameters": [ { "name": "n/a", "value": 40, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "A COC double-tap (DFON with tapCount=2) on the valence during weekday evenings triggers an evening lighting scene. Apply a short ramp (6.5s = enum value 25) before setting levels to ensure smooth transitions; actions target only the valence and track devices per device boundaries."
  }
}
__END_NUCORE_ROUTINE__
