__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday-morning raise heat setpoint when mode Heat and temp low",
    "parent": 0,
    "comment": "Increase heat setpoint to 72°F when mode is Heat and temp below 68°F on weekdays 6-9AM",
    "if": [
      { "==": { "device": "25 61 87 1", "status": "CLIMD", "value": 1, "uom": "98", "precision": 0 } },
      { "and": 1 },
      { "<": { "device": "25 61 87 1", "status": "ST", "value": 680, "uom": "14", "precision": 1 } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "06:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "25 61 87 1", "command": "CLISPH", "parameters": [ { "name": "n/a", "value": 720, "uom": "14", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "When thermostat mode is set to Heat during weekday mornings and the measured temperature is below 68.0°F (value 680 with precision 1), raise the heat setpoint to 72.0°F (value 720) to maintain comfort. Using CLISPH on the Thermostat - Main."
  }
}
__END_NUCORE_ROUTINE__
