__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Enable relay when desk light fast-on during work hours",
    "parent": 0,
    "comment": "COC from desk light during weekdays powers up relay",
    "if": [
      { "==": { "device": "38 76 ED 1", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY010_002_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (physical Fast On DFON from the Shop Office Desk Light) combined with a weekday time window. When the user fast-ons the desk light during work hours, send DON to the parent relay (ZY010_002_1) to power associated circuits. Empty 'else' because no alternate action required."
  }
}
__END_NUCORE_ROUTINE__
