__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening switch press -> warm light + chime",
    "parent": 0,
    "comment": "When ZWave switch is pressed ON between 20:00-23:00 set hue, enable enhanced hue, and play chime",
    "if": [
      { "==": { "device": "ZY003_1", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "20:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      {
        "device": "ZB31965_011_106",
        "command": "MOVETO",
        "parameters": [
          { "name": "Hue", "value": 30, "uom": "149", "precision": 0 },
          { "name": "Saturation", "value": 80, "uom": "51", "precision": 1 },
          { "name": "Duration", "value": 2, "uom": "58", "precision": 1 }
        ]
      },
      {
        "device": "ZB31965_011_106",
        "command": "EHUE",
        "parameters": [
          { "name": "n/a", "value": 100, "uom": "78", "precision": 0 }
        ]
      },
      {
        "device": "n004_0",
        "command": "PLAY",
        "parameters": [
          { "name": "n/a", "value": 5, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "COC on ZWave switch (DON) during the specified evening window triggers immediate scene: use MOVETO to set human-friendly hue and saturation with a short duration, enable Enhanced Hue for device behavior, and play chime via AudioPlayer. All commands and UOMs come from DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
