__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday arrival: set climate Home and heat 70 when sensor occupancy detected after 18:00",
    "parent": 0,
    "comment": "When someone arrives on weekdays after 18:00, switch to Home and set heat; otherwise ensure Away",
    "if": [
      { "(": 1 },
        { "==": { "device": "n002_s421800120477", "status": "GV1", "value": 1, "uom": "25", "precision": 0 } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:59" } } } },
      { ")": 1 }
    ],
    "then": [
      {
        "device": "n002_t421800120477",
        "command": "GV3",
        "parameters": [
          { "name": "n/a", "value": 1, "uom": "25", "precision": 0 }
        ]
      },
      {
        "device": "n002_t421800120477",
        "command": "CLISPH",
        "parameters": [
          { "name": "n/a", "value": 70, "uom": "17", "precision": 1 }
        ]
      }
    ],
    "else": [
      {
        "device": "n002_t421800120477",
        "command": "GV3",
        "parameters": [
          { "name": "n/a", "value": 0, "uom": "25", "precision": 0 }
        ]
      }
    ],
    "reasoning": "Trigger on sensor occupancy (GV1 == 1) during weekdays at or after 18:00. When true, set Climate Type (GV3) to Home (enum 1) and set heat setpoint (CLISPH) to 70°F. Otherwise set Climate Type to Away (enum 0). Uses only declared device IDs, commands, and UOMs from the DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
