__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Enable preconditioning when cold or snow during daytime",
    "parent": 0,
    "comment": "Enable AUTOCON when outdoor temp < 40F or forecast snow, only between 05:00-22:00",
    "if": [
      { "from": { "time":"05:00","to":{ "time":"22:00","day":0 } } },
      { "and": 1 },
      { "(": 1 },
        { "<": { "device":"n007_climate5rf7219","status":"GV2","value":40,"uom":"17","precision":1 } },
        { "or": 1 },
        { "==": { "device":"n002_f421800120477","status":"GV9","value":10,"uom":"25","precision":0 } },
      { ")": 1 }
    ],
    "then": [
      {
        "device":"n007_climate5rf7219",
        "command":"AUTOCON",
        "parameters":[ { "name":"n/a","value":1,"uom":"25","precision":0 } ]
      }
    ],
    "else": [
      {
        "device":"n007_climate5rf7219",
        "command":"AUTOCON",
        "parameters":[ { "name":"n/a","value":0,"uom":"25","precision":0 } ]
      }
    ],
    "reasoning": "Turn on vehicle preconditioning (AUTOCON) when it is cold outside (GV2 < 40F) or the forecast predicts snow (Forecast GV9 == 10), constrained to daytime hours 05:00-22:00 to avoid running overnight."
  }
}
__END_NUCORE_ROUTINE__
