__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Start overnight charge when SOC <20%",
    "parent": 0,
    "comment": "Set max current and start charging between 22:00 and 06:00 when SOC low",
    "if": [
      { "(": 1 },
        { "<": { "device": "n007_chargea5rf7219", "status": "ST", "value": 20, "uom": "51", "precision": 0 } },
        { "and": 1 },
        { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "n007_chargea5rf7219", "command": "CHARGEAMPS", "parameters": [ { "name": "n/a", "value": 32, "uom": "1", "precision": 0 } ] },
      { "device": "n007_chargea5rf7219", "command": "CHARGECTRL", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Separate routine triggers only at night when SOC falls below 20%. It first limits charging current to 32A (CHARGEAMPS) then issues CHARGECTRL start. Using time window ensures charging only overnight."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Stop charging when SOC >=80%",
    "parent": 0,
    "comment": "Stop charging immediately when battery reaches target",
    "if": [
      { ">=": { "device": "n007_chargea5rf7219", "status": "ST", "value": 80, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "n007_chargea5rf7219", "command": "CHARGECTRL", "parameters": [ { "name": "n/a", "value": 0, "uom": "25", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Independent routine monitors SOC and stops charging when SOC is >=80% to prevent overcharging."
  }
}
__END_NUCORE_ROUTINE__
