__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday daytime scene query",
    "parent": 0,
    "comment": "If Scene Button 3 triple-press OR Scene Button 4 fast-on during weekdays 09:00-17:00, query the basic assoc device",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY037_201N003", "control": "DON3" } },
        { "or": 1 },
        { "==": { "device": "ZY037_201N004", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "ZY037_252N003", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC triggers DON3 (triple-press) and DFON (fast-on) grouped and constrained to a weekly daytime window; action issues QUERY to ZY037_252N003 which accepts QUERY."
  }
}
__END_NUCORE_ROUTINE__
