__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Close garage if high chance of rain at sunrise",
    "parent": 0,
    "comment": "Close garage doors before sunrise when rain likely",
    "if": [
      { "at": { "sunrise": -900 } },
      { "and": 1 },
      { "(": 1 },
        { ">=": { "device": "n004_forecast_1", "status": "GV18", "value": 60, "uom": "22", "precision": 0 } },
        { "or": 1 },
        { ">=": { "device": "n004_forecast_2", "status": "GV18", "value": 60, "uom": "22", "precision": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "70 87 AA 4", "command": "WDU", "parameters": [ { "name": "ST", "value": 0, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Run 15 minutes before sunrise; if either forecast shows >=60% chance of rain, write ST=0 (off/closed) to the garage device using its Write Changes (WDU) command. Uses forecast chance-of-rain properties (GV18) with uom 22 and garage ST with uom 51."
  }
}
__END_NUCORE_ROUTINE__
