__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday Fast-On -> 70% and Stop Fades",
    "parent": 0,
    "comment": "When user performs Fast On during weekday daytime, set stable brightness and cancel fades",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY037_005_1", "control": "DFON" } },
        { "and": 1 },
        { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZY037_005_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 70, "uom": "51", "precision": 0 } ] },
      { "device": "ZY037_005_1", "command": "FDSTOP", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from the White Dimmer combined with a weekday schedule. Then issue a DON with 70% (uom 51) to set brightness and FDSTOP to cancel any ongoing fades so the level remains stable."
  }
}
__END_NUCORE_ROUTINE__
