__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Scene1 Triple-Press -> Warm White 5s",
    "parent": 0,
    "comment": "Triple press on Scene Button 1 sets Color Switch to warm white full with 5s fade",
    "if": [
      { "==": { "device": "ZY041_201N001", "control": "DON3" } }
    ],
    "then": [
      {
        "device": "ZY041_186",
        "command": "DON",
        "parameters": [
          { "name": "GV0", "value": 255 },
          { "name": "GV2", "value": 0 },
          { "name": "GV3", "value": 0 },
          { "name": "GV4", "value": 0 },
          { "name": "RR", "value": 5, "uom": "58", "precision": 0 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Use Scene Button 1 triple-press (send command DON3) as a COC trigger. The Color Switch DON command sets Warm White (GV0) to full and other channels off, with RR=5 seconds (uom 58) to create the requested 5s fade."
  }
}
__END_NUCORE_ROUTINE__
