__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset physical ON → gentle fade up",
    "parent": 0,
    "comment": "When the Green Dimmer is physically turned on near sunset, fade up smoothly",
    "if": [
      { "at": { "sunset": 600 } },
      { "and": 1 },
      { "==": { "device": "ZY041_003_1", "control": "DON" } }
    ],
    "then": [
      { "device": "ZY041_003_1", "command": "FDUP", "parameters": [
        { "name": "Start Level", "value": 1, "uom": "25", "precision": 0 },
        { "name": "Fade Rate", "value": 2, "uom": "45", "precision": 0 }
      ] }
    ],
    "else": [],
    "reasoning": "Listen for a physical ON (COC) on the Green Dimmer at sunset +10 minutes; when triggered, send FDUP using 'Previous Value' for Start Level and a 2-minute Fade Rate to create a smooth evening activation."
  }
}
__END_NUCORE_ROUTINE__
