__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Start pumps when waterfall is on at sunset",
    "parent": 0,
    "comment": "If Big Waterfall Top is on at sunset, start both Back Well pumps; otherwise ensure they are off.",
    "if": [
      { "(": 1 },
        { "==": { "device": "58 12 A2 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "and": 1 },
        { "at": { "sunset": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "58 11 FF 1", "command": "DON" },
      { "device": "58 11 FF 2", "command": "DON" }
    ],
    "else": [
      { "device": "58 11 FF 1", "command": "DOF" },
      { "device": "58 11 FF 2", "command": "DOF" }
    ],
    "reasoning": "Trigger checks the waterfall status at sunset; when On, start both pumps to supply it; otherwise stop pumps to conserve resources. Uses device ST enum (uom 51) and sunset schedule."
  }
}
__END_NUCORE_ROUTINE__
