__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Query channel_3 and channel_4 when channel_2 power spike after sunset or manual query",
    "parent": 0,
    "comment": "Poll other channels when channel_2 CPW >= 5000 (precision 2 => 50.00) after sunset or when channel_2 Query control is invoked",
    "if": [
      { "(": 1 },
        { ">=": { "device": "n015_300336_2", "status": "ST", "value": 5000, "uom": "30", "precision": 2 } },
        { "and": 1 },
        { "at": { "sunset": 0 } },
      { ")": 1 },
      { "or": 1 },
      { "==": { "device": "n015_300336_2", "control": "QUERY" } }
    ],
    "then": [
      { "device": "n015_300336_3", "command": "Query", "parameters": [] },
      { "device": "n015_300336_4", "command": "Query", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger when channel_2 has a COS with CPW >= 50.00 kW after sunset, or when the user issues a COC (manual Query) on channel_2. Then poll channel_3 and channel_4 to record their energy values." 
  }
}
__END_NUCORE_ROUTINE__
