__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night - Front Door Open Beep Heartbeat",
    "parent": 0,
    "comment": "Beep heartbeat when main door opens at night",
    "if": [
      { "==": { "device": "32 8B 66 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "32 8B 66 4", "command": "BEEP" }
    ],
    "else": [],
    "reasoning": "Trigger on a COS of the main sensor status to On (value 100) and restrict to nightly hours; use the Heartbeat child’s BEEP command (accepted by that device) to alert. No action required otherwise."
  }
}
__END_NUCORE_ROUTINE__
