__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night Quick-On: E DFON triggers H on and G off",
    "parent": 0,
    "comment": "When GRMini Remote- E is fast-on during overnight hours, update H and G.",
    "if": [
      { "==": { "device":"44 AD 7B 6","control":"DFON" } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"44 AD 7B 7","command":"WDU" },
      { "device":"44 AD 7B 8","command":"WDU" }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) from device 44 AD 7B 6 and restrict to the overnight schedule (22:00 to 06:00 day wrap). Then call WDU on H and G to apply the desired state changes via their accepted Write Changes command."
  }
}
__END_NUCORE_ROUTINE__
