__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weeknight double-tap: WineBar on + UnderCab backlight 80%",
    "parent": 0,
    "comment": "Trigger: fast on control (DFON) on UnderCab during weeknights 18:00-23:00",
    "if": [
      { "(": 1 },
        { "==": { "device": "51 AF EF 1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "23:00" } } } }
    ],
    "then": [
      { "device": "51 AF EF 2", "command": "DON" },
      { "device": "51 AF EF 1", "command": "BL", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 2 } ] }
    ],
    "else": [],
    "reasoning": "User requested action on a COC (fast on DFON) constrained to weeknights 18:00-23:00. The IF groups a DFON control event on device 51 AF EF 1 with a weekly time window. THEN turns on WineBar (DON) and sets UnderCab backlight (BL) to 80% using uom 51 as defined in device structure. No ELSE actions specified." 
  }
}
__END_NUCORE_ROUTINE__
