__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday 18:00 -> Set Color to 2700K if below",
    "parent": 0,
    "comment": "Set lamp to warm white on weekdays at 18:00 only when cooler than target",
    "if": [
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "18:00" } } } },
      { "and": 1 },
      { "<": { "device": "ZB23808_011_108", "status": "GV2", "value": 2700, "uom": "26", "precision": 1 } }
    ],
    "then": [
      {
        "device": "ZB23808_011_108",
        "command": "MOVETOCT",
        "parameters": [
          { "name": "Color", "value": 2700, "uom": "26", "precision": 1 },
          { "name": "Duration", "value": 5, "uom": "58", "precision": 1 }
        ]
      }
    ],
    "else": [],
    "reasoning": "Trigger only on weekdays at 18:00 and act only when the current color temperature (GV2) is below 2700K to avoid unnecessary transitions. Use MOVETOCT with COLOR (Kelvin) and Duration as defined in the device structure."
  }
}
__END_NUCORE_ROUTINE__
