__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Leak detected - alarm and query",
    "parent": 0,
    "comment": "",
    "if": [
      { "(": 1 },
        { "==": { "device": "28 8 4 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "28 8 4 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "28 8 4 1", "command": "BEEP" },
      { "device": "28 8 4 2", "command": "BEEP" },
      { "device": "28 8 4 1", "command": "QUERY" },
      { "device": "28 8 4 2", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "When either water leak sensor reports ON (wet), immediately sound beeps on both leak devices and query their state to confirm; no action otherwise."
  }
}
__END_NUCORE_ROUTINE__
