__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night doorbell -> closet beep and query",
    "parent": 0,
    "comment": "When doorbell activates overnight, alert and check closet status",
    "if": [
      { "==": { "device":"28 C7 65 2","status":"ST","value":100,"uom":"51","precision":0 } },
      { "and": 1 },
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } }
    ],
    "then": [
      { "device":"28 C7 65 1","command":"BEEP" },
      { "device":"28 C7 65 1","command":"QUERY" }
    ],
    "else": [],
    "reasoning": "Detect when Doorbell Chime status changes to On during overnight hours; then command the Closet Door to beep (alert) and perform a status query to report its state. Uses Doorbell ST COS and an overnight schedule."
  }
}
__END_NUCORE_ROUTINE__
