__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Overnight patio-door open or DFON → notify ZM and update closed sensor",
    "parent": 0,
    "comment": "Trigger on COS (door opened) or COC (fast on) only during overnight hours",
    "if": [
      { "(": 1 },
        { "==": { "device": "2D 72 81 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "2D 72 81 2", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } }
    ],
    "then": [
      { "device": "ZM00002_011_1", "command": "QUERY" },
      { "device": "2D 72 81 2", "command": "WDU" }
    ],
    "else": [
      { "device": "2D 72 81 1", "command": "QUERY" }
    ],
    "reasoning": "Group COS (patio door status == On) and COC (closed sensor DFON) with an overnight schedule (22:00–06:00). When either trigger happens during the scheduled window query the ZM module (status/diagnostics) and write changes to the closed sensor; otherwise just query the parent door for state. Uses only accepted commands: QUERY and WDU."
  }
}
__END_NUCORE_ROUTINE__
