__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Beep at sunset when patio door opens",
    "parent": 0,
    "comment": "",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "2D 77 B5 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "2D 77 B5 1", "command": "BEEP" },
      { "device": "2D 77 B5 2", "command": "QUERY" }
    ],
    "else": [],
    "reasoning": "Trigger at sunset when the Bedroom Patio Door-Closed reports On (opened). Notify by sending BEEP to the Gate Doobell and refresh the door state with QUERY. Using the sunset schedule prevents daytime triggers."
  }
}
__END_NUCORE_ROUTINE__
