__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Evening physical ON -> set white dimmer 50%",
    "parent": 0,
    "comment": "When the white dimmer is physically turned on in the evening, set level to 50%.",
    "if": [
      { "==": { "device": "ZY041_005_1", "control": "DON" } },
      { "and": 1 },
      { "from": { "time": "17:00", "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "ZY041_005_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 50, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "COC: Use the white dimmer's On send event (DON) as the physical control trigger. Restrict to the 17:00-23:00 window. Action uses the On command's level parameter (uom 51) to set 50%."
  }
}
__END_NUCORE_ROUTINE__
