__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Link 1 ON to 2 & 3 at evening",
    "parent": 0,
    "comment": "When 1 is physically turned on after sunset and before 23:00, enable 2 and 3 with specified temp/brightness",
    "if": [
      { "==": { "device": "n013_88010d247ff20b", "control": "DON" } },
      { "and": 1 },
      { "from": { "sunset": 0, "to": { "time": "23:00", "day": 0 } } }
    ],
    "then": [
      { "device": "n013_88010d23cb7c0b", "command": "DON" },
      { "device": "n013_88010d23cb7c0b", "command": "SET_CTBR", "parameters": [ { "name": "K", "value": 5000, "uom": "26", "precision": 1 }, { "name": "D", "value": 1000, "uom": "42", "precision": 1 }, { "name": "BR", "value": 200, "uom": "100", "precision": 1 } ] },
      { "device": "n013_88010e12f2b50b", "command": "DON" },
      { "device": "n013_88010e12f2b50b", "command": "SET_CTBR", "parameters": [ { "name": "K", "value": 5000, "uom": "26", "precision": 1 }, { "name": "D", "value": 1000, "uom": "42", "precision": 1 }, { "name": "BR", "value": 200, "uom": "100", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (physical ON) from device 1 combined with a daily sunset-to-23:00 schedule to ensure this automation only runs in the evening. Then explicitly turn on devices 2 and 3 and set color temperature + brightness using SET_CTBR with a 1s transition to meet the user's requirement. Values chosen for BR are within the device BR parameter range (1-254) and K within 2000-6500K."
  }
}
__END_NUCORE_ROUTINE__
