__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night Dog Detection - Ensure On and Tag",
    "parent": 0,
    "comment": "When Dog is detected overnight, ensure camera is on and send Dog tag",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "n003_01_004_animal", "status": "GV4", "value": 1, "uom": "2", "precision": 0 } }
    ],
    "then": [
      { "device": "n003_01_004_animal", "command": "On", "parameters": [] },
      { "device": "n003_01_004_animal", "command": "Dog", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Trigger is a COS for the Dog property (GV4 == 1) limited to overnight hours. Action ensures the camera is On and sends the Dog tag command. Using uom 2 and precision 0 as defined in DEVICE STRUCTURE."
  }
}
__END_NUCORE_ROUTINE__
