__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday morning Art DFON -> Hue Play warm",
    "parent": 0,
    "comment": "When Art is fast-on during weekday mornings set Hue Play 4 warm and bright over 5s",
    "if": [
      { "(": 1 },
        { "==": { "device": "n013_huegrp86", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "07:00", "to": { "time": "09:00" } } } }
    ],
    "then": [
      { "device": "n013_88010c6b3e7c0b", "command": "SET_CTBR", "parameters": [ { "name": "K", "value": 3000, "uom": "26", "precision": 1 }, { "name": "D", "value": 5000, "uom": "42", "precision": 1 }, { "name": "BR", "value": 200, "uom": "100", "precision": 1 } ] },
      { "device": "n013_88010c6b3e7c0b", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COC trigger (DFON) on the Art group limited to weekday mornings. Then set Hue Play 4 with SET_CTBR to 3000K, 200 brightness with a 5000ms transition, then issue DON to ensure the lamp is on."
  }
}
__END_NUCORE_ROUTINE__
