__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Close big blinds at sunset if responding",
    "parent": 0,
    "comment": "Close big blinds at sunset; if not responding, query the physical switch",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "==": { "device": "ZY024_1", "status": "ERR", "value": 0, "uom": "25" } }
    ],
    "then": [
      { "device": "ZY024_1", "command": "DOF", "parameters": [] }
    ],
    "else": [
      { "device": "ZY024_147", "command": "QUERY", "parameters": [] }
    ],
    "reasoning": "Run at sunset; only send DOF to the big blinds if they report Responding==True (ERR=0). If not responding, query the physical switch to attempt a state refresh."
  }
}
__END_NUCORE_ROUTINE__
