__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Notify on REST Receive Error",
    "parent": 0,
    "comment": "Send UD Mobile push when controller REST Status becomes Receive Error",
    "if": [
      { "==": { "device": "n012_controller", "status": "GV1", "value": 3, "uom": "25", "precision": 0 } }
    ],
    "then": [
      { "device": "n012_udmobile", "command": "GV10", "parameters": [
          { "name": "Group", "value": 1, "uom": "25", "precision": 0 },
          { "name": "Sound", "value": 2, "uom": "25", "precision": 0 }
      ] }
    ],
    "else": [],
    "reasoning": "Immediate push notification when GV1 (REST Status) becomes Receive Error (3). Use UD Mobile GV10 Send Message with Group=1 and Sound=2 (SMS Notification)."
  }
}
__END_NUCORE_ROUTINE__
