__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Open front door after sunset - query children",
    "parent": 0,
    "comment": "When front door opens after sunset, query heartbeat and low battery sensors",
    "if": [
      { "==": { "device": "32 8B 66 1", "status": "ST", "value": 100, "uom": "51", "precision": 0 } },
      { "and": 1 },
      { "at": { "sunset": 600 } }
    ],
    "then": [
      { "device": "32 8B 66 4", "command": "QUERY", "parameters": [] },
      { "device": "32 8B 66 3", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use a COS on the main sensor ST to On combined with a sunset offset to limit to after-sunset openings. Then query child sensors to refresh status for automation or alerts."
  }
}
__END_NUCORE_ROUTINE__
