__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Night presence — set fan to Low",
    "parent": 0,
    "comment": "Turn fan to Low when sensor detects presence overnight",
    "if": [
      { "from": { "time":"22:00","to":{ "time":"06:00","day":1 } } },
      { "and": 1 },
      { "==": { "device":"2F 8E 31 1","control":"DON" } }
    ],
    "then": [
      { "device":"14 A7 6F 2","command":"DON","parameters":[ { "name":"n/a","value":25,"uom":"51","precision":0 } ] }
    ],
    "else": [],
    "reasoning": "Use the sensor's ON send-command (DON) as a control event (COC) combined with an overnight schedule (22:00-06:00) so the fan only turns on at night. Set the Bedroom Fan-Motor to Low using its DON command and the enum value 25 (uom 51)."
  }
}
__END_NUCORE_ROUTINE__
