__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "E fast-on on weekdays sets H on",
    "parent": 0,
    "comment": "When E receives a Fast On during work hours, write H status to On.",
    "if": [
      { "==": { "device": "26 87 6F 6", "control": "DFON" } },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
    ],
    "then": [
      { "device": "26 87 6F 7", "command": "WDU", "parameters": [ { "name": "ST", "value": 100, "uom": "51", "precision": 0 } ] }
    ],
    "else": [],
    "reasoning": "Use a COC (DFON) from keypad E combined with a weekday schedule 09:00-17:00 to trigger a write (WDU) setting keypad H's Status (ST) to 100%.">
  }
}
__END_NUCORE_ROUTINE__
