__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Nighttime Patio Door-Closed Alert",
    "parent": 0,
    "comment": "Beep closed sensor and query main ZM when the closed sensor transitions to On during night hours",
    "if": [
      { "from": { "time": "22:00", "to": { "time": "06:00", "day": 1 } } },
      { "and": 1 },
      { "==": { "device": "2D 72 81 2", "status": "ST", "value": 100, "uom": "51", "precision": 0 } }
    ],
    "then": [
      { "device": "2D 72 81 2", "command": "BEEP", "parameters": [] },
      { "device": "ZM00002_011_1", "command": "QUERY", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Limit action to night using a from/to schedule that spans midnight; trigger on the closed-child device ST==100 (On) to represent a COS to closed. Use BEEP (accepted by the closed device) and QUERY the main ZM (accepted by ZM00002_011_1)."
  }
}
__END_NUCORE_ROUTINE__
