__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Master Bedroom: fast-on at sunset → warm scene",
    "parent": 0,
    "comment": "Activate warm wake scene when user fast-ons at sunset",
    "if": [
      { "==": { "device": "n001_huegrp81", "control": "DFON" } },
      { "and": 1 },
      { "at": { "sunset": 0 } }
    ],
    "then": [
      { "device": "n001_huegrp81", "command": "SET_CTBR", "parameters": [ { "name": "K", "value": 3000, "uom": "26", "precision": 1 }, { "name": "D", "value": 5000, "uom": "42", "precision": 1 }, { "name": "BR", "value": 200, "uom": "100", "precision": 1 } ] }
    ],
    "else": [],
    "reasoning": "User taps fast-on in Master Bedroom at sunset: treat that control (COC) plus sunset schedule to set a warm 3000K with medium-high brightness and a 5s transition for a comfortable arrival.">
  }
}
__END_NUCORE_ROUTINE__
