__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Sunset: query non-responding or nightly query",
    "parent": 0,
    "comment": "If any device is not responding at sunset query them; otherwise query nightly at 22:00",
    "if": [
      { "at": { "sunset": 0 } },
      { "and": 1 },
      { "(": 1 },
        { "==": { "device": "ZM00002_029_1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "ZM00002_030_1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } },
        { "or": 1 },
        { "==": { "device": "ZM00002_031_1", "status": "ERR", "value": 1, "uom": "25", "precision": 0 } },
      { ")": 1 }
    ],
    "then": [
      { "device": "ZM00002_029_1", "command": "QUERY", "parameters": [] },
      { "device": "ZM00002_030_1", "command": "QUERY", "parameters": [] },
      { "device": "ZM00002_031_1", "command": "QUERY", "parameters": [] }
    ],
    "else": [
      { "device": "ZM00002_029_1", "command": "QUERY", "parameters": [] },
      { "device": "ZM00002_030_1", "command": "QUERY", "parameters": [] },
      { "device": "ZM00002_031_1", "command": "QUERY", "parameters": [] }
    ],
    "reasoning": "At sunset check if any device Responding==False and query them. If the sunset condition is met but none are false then nightly queries at 22:00 are performed via else path (scheduled separately by the system trigger)."
  }
}
__END_NUCORE_ROUTINE__
