__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 101,
    "name": "Weekday fast-on sets blue dimmer to 80%",
    "parent": 0,
    "comment": "When Blue Dimmer fast-on between 18:00-22:00 mon-fri set 80%, else restore previous",
    "if": [
      { "(": 1 },
        { "==": { "device": "ZY037_004_1", "control": "DFON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "18:00", "to": { "time": "22:00" } } } }
    ],
    "then": [
      { "device": "ZY037_004_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 80, "uom": "51", "precision": 0 } ] }
    ],
    "else": [
      { "device": "ZY037_004_1", "command": "DON", "parameters": [ { "name": "n/a", "value": 1, "uom": "25", "precision": 0 } ] }
    ],
    "reasoning": "Use COC for fast on (DFON) and limit to weekday evening window; set explicit percent when in window, otherwise restore previous using enum value 1."
  }
}
__END_NUCORE_ROUTINE__
