__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "On press of assoc 8 query assoc 7 and 9",
    "parent": 0,
    "comment": "COC: physical ON of ZY041_258N008 triggers queries",
    "if": [
      { "==": { "device":"ZY041_258N008","control":"DON" } }
    ],
    "then": [
      { "device":"ZY041_252N007", "command":"QUERY", "parameters":[] },
      { "device":"ZY041_252N009", "command":"QUERY", "parameters":[] }
    ],
    "else": [],
    "reasoning": "A physical ON (COC) from the dimmer assoc 8 indicates user control. Immediately query assoc 7 and assoc 9 (both accept QUERY) to synchronize their reported states with the controller."
  }
}
__END_NUCORE_ROUTINE__
