__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 1,
    "name": "Weekday scene press turns compressor on",
    "parent": 0,
    "comment": "Turn compressor on when either scene controller sends an On during weekdays between 08:00-18:00",
    "if": [
      { "(": 1 },
      { "==": { "device": "ZY028_252N002", "control": "DON" } },
      { "or": 1 },
      { "==": { "device": "ZY028_252N003", "control": "DON" } },
      { ")": 1 },
      { "and": 1 },
      { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "08:00", "to": { "time": "18:00" } } } }
    ],
    "then": [
      { "device": "ZY028_1", "command": "DON", "parameters": [] }
    ],
    "else": [],
    "reasoning": "Use COC from scene controllers confined to weekday hours to avoid accidental activations evenings/weekends."
  }
}
__END_NUCORE_ROUTINE__

__BEGIN_NUCORE_ROUTINE__
{
  "routine": {
    "id": 2,
    "name": "Daily compressor off at 18:00",
    "parent": 0,
    "comment": "Ensure compressor is turned off at 18:00 every day",
    "if": [
      { "at": { "time": "18:00" } }
    ],
    "then": [
      { "device": "ZY028_1", "command": "DOF", "parameters": [] }
    ],
    "else": [],
    "reasoning": "A daily scheduled off to enforce shutdown regardless of manual presses."
  }
}
__END_NUCORE_ROUTINE__
