{"routine": {"id": 1,"name": "Turn on BSMT2-TEST EQUIP when motion detected on weekdays 9-17","parent": 0,"if": [
  {"IS": {"device": "ZY170_380", "control": "DON"}},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}}
],"then": [
  {"device": "ZY006_1", "command": "DON", "parameters": [{"name": "n/a", "value": 100, "uom": "78"}]}
],"else": [],"reasoning": "Activate basement equipment when motion is detected during working hours on weekdays."}}

{"routine": {"id": 2,"name": "Keep ZY 170 Wake Up device awake on weekends nights","parent": 0,"if": [
  {"IS": {"device": "ZY170_199", "control": "DON"}},
  {"and": 1},
  {"weekly": {"days": "sat,sun", "from": {"time": "22:00", "to": {"time": "06:00", "day": 1}}}}
],"then": [
  {"device": "ZY170_199", "command": "DON", "parameters": [{"name": "n/a", "value": 100, "uom": "78"}]}
],"else": [
  {"device": "ZY170_199", "command": "DOF"}
],"reasoning": "Maintain the wake state of the device during weekend night hours."}}

{"routine": {"id": 3,"name": "Turn off BSMT2-TEST EQUIP if no motion for 30 min 8-20","parent": 0,"if": [
  {"IS": {"device": "ZY170_380", "control": "DOF"}},
  {"and": 1},
  {"from": {"time": "08:00", "to": {"time": "20:00"}}}
],"then": [
  {"device": "ZY006_1", "command": "DOF"}
],"else": [],"reasoning": "Deactivate basement equipment if motion sensor is off during daytime hours."}}
