{"routine": {"id": 1,"name": "Turn on SPARE2 switch on bathroom motion weekdays 9-17","parent": 0,"if": [
  {"IS": {"device": "ZY172_155", "control": "DON"}},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}}
],"then": [
  {"device": "28 36 FB 1", "command": "DON", "parameters": [{"name": "n/a", "value": 100, "uom": "51"}]}
],"else": [],"reasoning": "Activate SPARE2 switch when bathroom motion sensor detects motion during weekdays 9 AM to 5 PM."}}

{"routine": {"id": 2,"name": "Turn on SPARE2 if garage temp above 75F between sunrise and sunset","parent": 0,"if": [
  {"GE": {"device": "ZY083_118", "status": "CLITEMP", "value": 7500, "uom": "fahrenheit", "precision": 2}},
  {"and": 1},
  {"from": {"sunrise": 0, "to": {"sunset": 0}}}
],"then": [
  {"device": "28 36 FB 1", "command": "DON", "parameters": [{"name": "n/a", "value": 100, "uom": "51"}]}
],"else": [],"reasoning": "Activate SPARE2 switch when garage tool room temperature is 75°F or higher during daytime."}}

