  {"IS": {"device": "ZY172_155", "control": "DOF"}},
  {"and": 1},
  {"from": {"time": "09:00", "to": {"time": "18:00", "day": 1}}}
],"then": [
  {"device": "28 36 FB 1", "command": "DOF"}
],"else": [],"reasoning": "Deactivate SPARE2 switch when bathroom motion sensor is off for 30 minutes during weekdays 9 AM to 6 PM."}}
