{"routine": {"id": 1,"name": "Turn off all switches at sunset if power above 1000","parent": 0,"if": [
  { "at": { "sunset": 0 } },
  { "and": 1 },
  { "GE": { "device": "ZY002_118", "status": "CPW", "value": 1000, "uom": "count", "precision": 0 } }
],"then": [
  { "device": "ZY002_183", "command": "ALLOFF" }
],"else": []}}

{"routine": {"id": 2,"name": "Weekday switch control based on power consumption","parent": 0,"if": [
  { "weekly": { "days": "mon,tue,wed,thu,fri", "from": { "time": "09:00", "to": { "time": "17:00" } } } }
],"then": [
  { "device": "ZY002_183", "command": "ALLON" }
],"else": []}}

{"routine": {"id": 3,"name": "Turn off switches if power consumption high","parent": 0,"if": [
  { "GE": { "device": "ZY002_118", "status": "CPW", "value": 1500, "uom": "count", "precision": 0 } }
],"then": [
  { "device": "ZY002_183", "command": "ALLOFF" }
],"else": []}}

{"routine": {"id": 4,"name": "Turn on all switches on weekends after double manual on","parent": 0,"if": [
  { "IS": { "device": "ZY002_183", "control": "DFON", "parameters": [{ "name": "tapCount", "value": 2, "uom": "count", "precision": 0 }] } },
  { "and": 1 },
  { "weekly": { "days": "sat,sun", "from": { "time": "10:00", "to": { "time": "20:00" } } } }
],"then": [
  { "device": "ZY002_183", "command": "ALLON" }
],"else": []}}
