{"routine": {"id": 1,"name": "Notify to close windows on high temp or rain forecast weekdays 9-17","parent": 0,"if": [
  {"(": 1},
    {"GE": {"device": "n002_rs_qvgg", "status": "ST", "value": 800, "uom": "fahrenheit", "precision": 1}},
    {"or": 1},
    {"EQ": {"device": "n002_f521782496039", "status": "GV9", "value": 6, "uom": "enum", "precision": 0}},
  {"")": 1},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}}
],"then": [
  {"device": "notification_system", "command": "SendNotification", "parameters": [{"name": "message", "value": "Please close the windows due to high temperature or rain forecast."}]}
],"else": []}}

{"routine": {"id": 2,"name": "Notify to open blinds on occupancy and sunny weekends 10-16","parent": 0,"if": [
  {"(": 1},
    {"IS": {"device": "n002_rs_qvgg", "control": "GV1", "parameters": [{"name": "value", "value": 1, "uom": "enum", "precision": 0}]}},
    {"and": 1},
    {"EQ": {"device": "n002_w521782496039", "status": "GV9", "value": 0, "uom": "enum", "precision": 0}},
  {"")": 1},
  {"and": 1},
  {"weekly": {"days": "sat,sun", "from": {"time": "10:00", "to": {"time": "16:00"}}}}
],"then": [
  {"device": "notification_system", "command": "SendNotification", "parameters": [{"name": "message", "value": "Master bedroom occupied and sunny outside, please open the blinds."}]}
],"else": []}}

{"routine": {"id": 3,"name": "Turn on porch lights at sunset if cold and cloudy","parent": 0,"if": [
  {"at": {"sunset": 600}},
  {"and": 1},
  {"LT": {"device": "n002_w521782496039", "status": "ST", "value": 600, "uom": "fahrenheit", "precision": 1}},
  {"and": 1},
  {"EQ": {"device": "n002_f521782496039", "status": "GV7", "value": 17, "uom": "enum", "precision": 0}}
],"then": [
  {"device": "porch_lights", "command": "DON"}
],"else": []}}
