{"routine": {"id": 1,"name": "Leak Alarm and HVAC Cooling on Leak or High Temp During Work Hours","parent": 0,"if": [
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"} } }},
  {"and": 1},
  {"(": 1},
    {"GE": {"device": "55 A F6 4", "status": "ST", "value": 100, "uom": "51"}},
    {"or": 1},
    {"GE": {"device": "25 18 A2 1", "status": "ST", "value": 750, "uom": "fahrenheit", "precision": 1}},
  {"")": 1}
],"then": [
  {"device": "55 A F6 4", "command": "BEEP", "parameters": [{"name": "n/a", "value": 0, "uom": "100"}]},
  {"device": "25 18 A2 1", "command": "Mode", "parameters": [{"name": "n/a", "value": 2, "uom": "98"}]},
  {"device": "25 18 A2 1", "command": "Write Changes"}
],"else": [],"reasoning": "Activate kitchen leak alarm beep if leak detected and set HVAC to cooling if temperature exceeds 75F during work hours Mon-Fri 9AM-5PM."}}

{"routine": {"id": 2,"name": "Sunset Leak Alarm and Cooling Control Off","parent": 0,"if": [
  {"at": {"sunset": 600}},
  {"and": 1},
  {"GE": {"device": "55 A F6 4", "status": "ST", "value": 100, "uom": "51"}}
],"then": [
  {"device": "55 A F6 4", "command": "BEEP", "parameters": [{"name": "n/a", "value": 0, "uom": "100"}]},
  {"device": "25 18 A2 2", "command": "Off"}
],"else": []}}

{"routine": {"id": 3,"name": "Set HVAC Fan Mode Based on Leak Sensor and Cooling Mode Weekdays","parent": 0,"if": [
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"} } }},
  {"and": 1},
  {"GE": {"device": "25 18 A2 1", "status": "CLIMD", "value": 2, "uom": "98"}},
  {"and": 1},
  {"GE": {"device": "55 A F6 4", "status": "ST", "value": 0, "uom": "51"}}
],"then": [
  {"device": "25 18 A2 1", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 8, "uom": "99"}]},
  {"device": "25 18 A2 1", "command": "Write Changes"}
],"else": [
  {"device": "25 18 A2 1", "command": "Fan Mode", "parameters": [{"name": "n/a", "value": 7, "uom": "99"}]},
  {"device": "25 18 A2 1", "command": "Write Changes"}
]}}
