{"routine": {"id": 1,"name": "Fast On DoorSensor.3 and DoorSensor.4 when DoorSensor.Door.Sensor On Weekdays 9-17","parent": 0,"if": [
  {"IS": {"device": "DoorSensor.Door.Sensor", "control": "DON"}},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}}
],"then": [
  {"device": "DoorSensor.3", "command": "DFON"},
  {"device": "DoorSensor.4", "command": "DFON"}
],"else": []}}

{"routine": {"id": 2,"name": "Beep DoorSensor.4 and DoorSensor.Door.Sensor when DoorSensor.3 Off","parent": 0,"if": [
  {"IS": {"device": "DoorSensor.3", "control": "DOF"}}
],"then": [
  {"device": "DoorSensor.4", "command": "BEEP", "parameters": [{"name": "n/a", "value": 0, "uom": "100"}]},
  {"device": "DoorSensor.Door.Sensor", "command": "BEEP", "parameters": [{"name": "n/a", "value": 0, "uom": "100"}]}
],"else": []}}

{"routine": {"id": 3,"name": "Turn On DoorSensor.3 and DoorSensor.4 at 8:30 if DoorSensor.Door.Sensor Off","parent": 0,"if": [
  {"at": {"time": "08:30"}},
  {"and": 1},
  {"GE": {"device": "DoorSensor.Door.Sensor", "status": "ST", "value": 0, "uom": "51", "precision": 0}},
  {"and": 1},
  {"IS": {"device": "DoorSensor.Door.Sensor", "status": "ST", "value": 0}}
],"then": [
  {"device": "DoorSensor.3", "command": "DON"},
  {"device": "DoorSensor.4", "command": "DON"}
],"else": []}}
