{"routine": {"id": 1,"name": "Turn on all lights and notify on tamper sensor trigger during weekdays 9-17","parent": 0,"if": [
  {"IS": {"device": "ZY170_378", "control": "DON"}},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}}
],"then": [
  {"device": "ZY028_183", "command": "ALLON"},
  {"device": "n003_udmobile", "command": "GV10", "parameters": [
    {"name": "Group", "value": 0},
    {"name": "Sound", "value": 7},
    {"name": "Content", "value": "Tamper sensor triggered"}
  ]}
],"else": [],"reasoning": "Activate all lights and send alarm notification when tamper sensor triggers during working hours on weekdays."}}

{"routine": {"id": 2,"name": "Turn off all lights and notify on tamper sensor off during night","parent": 0,"if": [
  {"IS": {"device": "ZY170_378", "control": "DOF"}},
  {"and": 1},
  {"from": {"time": "22:00", "to": {"time": "06:00", "day": 1}}}
],"then": [
  {"device": "ZY028_183", "command": "ALLOFF"},
  {"device": "n003_udmobile", "command": "GV10", "parameters": [
    {"name": "Group", "value": 0},
    {"name": "Sound", "value": 4},
    {"name": "Content", "value": "Tamper sensor turned off at night"}
  ]}
],"else": []}}

{"routine": {"id": 3,"name": "Daily 8:30 AM check tamper sensor and notify with lights on","parent": 0,"if": [
  {"at": {"time": "08:30"}},
  {"and": 1},
  {"GE": {"device": "ZY170_378", "status": "ST", "value": 100, "uom": "78", "precision": 0}}
],"then": [
  {"device": "ZY028_183", "command": "ALLON"},
  {"device": "n003_udmobile", "command": "GV10", "parameters": [
    {"name": "Group", "value": 0},
    {"name": "Sound", "value": 8},
    {"name": "Content", "value": "Tamper sensor is on at 8:30 AM"}
  ]}
],"else": []}}
