{"routine": {"id": 1,"name": "Turn on ½BTH2 heater on motion detected in evening","parent": 0,"if": [
  {"(": 1},
    {"GE": {"device": "ZY171_380", "status": "ST", "value": 100, "uom": "78", "precision": 0}},
    {"and": 1},
    {"weekly": {"days": "sun,mon,tue,wed,thu,fri,sat", "from": {"time": "18:00", "to": {"time": "22:00"}}}},
  { ")": 1}
],"then": [
  {"device": "ZM00009_001_1", "command": "DON"}
],"else": [
  {"device": "ZM00009_001_1", "command": "DOF"}
]
}}

{"routine": {"id": 2,"name": "Turn on faucet heater on motion weekday mornings","parent": 0,"if": [
  {"(": 1},
    {"GE": {"device": "ZY171_380", "status": "ST", "value": 100, "uom": "78", "precision": 0}},
    {"and": 1},
    {"EQ": {"device": "ZM00002_001_1", "status": "ST", "value": 0, "uom": "78", "precision": 0}},
  { ")": 1},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "07:00", "to": {"time": "09:00"}}}}
],"then": [
  {"device": "ZM00002_001_1", "command": "DON"}
],"else": []
}}

{"routine": {"id": 3,"name": "Control ½BTH2 heater based on faucet heater and sun events","parent": 0,"if": [
  {"(": 1},
    {"EQ": {"device": "ZM00009_001_1", "status": "ST", "value": 100, "uom": "78", "precision": 0}},
    {"and": 1},
    {"EQ": {"device": "ZM00002_001_1", "status": "ST", "value": 0, "uom": "78", "precision": 0}},
  { ")": 1},
  {"and": 1},
  {"at": {"sunset": 0}}
],"then": [
  {"device": "ZM00009_001_1", "command": "DOF"}
],"else": []
}}

{"routine": {"id": 4,"name": "Turn on ½BTH2 heater at sunrise if faucet heater off","parent": 0,"if": [
  {"EQ": {"device": "ZM00002_001_1", "status": "ST", "value": 0, "uom": "78", "precision": 0}},
  {"and": 1},
  {"at": {"sunrise": 0}}
],"then": [
  {"device": "ZM00009_001_1", "command": "DON"}
],"else": []
}}
