{"routine": {"id": 1,"name": "Activate ZY 185 SC Basic Control on Tamper Alarm Weekdays 9-17","parent": 0,"if": [
  {"(": 1},
    {"GE": {"device": "ZY201_157", "status": "ST", "value": 100, "uom": "78", "precision": 0}},
  {")": 1},
  {"and": 1},
  {"weekly": {"days": "mon,tue,wed,thu,fri", "from": {"time": "09:00", "to": {"time": "17:00"}}}}
],"then": [
  {"device": "ZY185_251", "command": "DON"}
],"else": [],"reasoning": "Turn on the basic control device when tamper alarm is On during working weekdays hours."}}

{"routine": {"id": 2,"name": "Activate ZY 185 SC Basic Control on Alarm or Tamper 8-18 Daily","parent": 0,"if": [
  {"(": 1},
    {"GE": {"device": "ZY201_1", "status": "ALARM", "value": 1, "uom": "bool", "precision": 0}},
    {"or": 1},
    {"GE": {"device": "ZY201_157", "status": "ST", "value": 100, "uom": "78", "precision": 0}},
  {")": 1},
  {"and": 1},
  {"from": {"time": "08:00", "to": {"time": "18:00"}}}
],"then": [
  {"device": "ZY185_251", "command": "DON"}
],"else": [
  {"device": "ZY185_251", "command": "DOF"}
],"reasoning": "Turn on basic control if home security alarm is active or tamper alarm is On during daytime, else turn off."}}

{"routine": {"id": 3,"name": "Weekend Control of ZY 185 SC Basic Control on Alarm or Tamper","parent": 0,"if": [
  {"weekly": {"days": "sat,sun", "from": {"time": "10:00", "to": {"time": "16:00"}}}},
  {"and": 1},
  {"(": 1},
    {"GE": {"device": "ZY201_157", "status": "ST", "value": 100, "uom": "78", "precision": 0}},
    {"or": 1},
    {"GE": {"device": "ZY201_1", "status": "ALARM", "value": 1, "uom": "bool", "precision": 0}},
  {")": 1}
],"then": [
  {"device": "ZY185_251", "command": "DON"}
],"else": [
  {"device": "ZY185_251", "command": "DOF"}
],"reasoning": "Activate basic control on weekends during specified hours if tamper alarm is On or home security alarm is active; otherwise turn off."}}
